import { shade } from 'polished';
import styled from 'styled-components';

export const Container = styled.div`
  height: 100vh;
`;

export const Content = styled.div`
  input {
    background: #dcd0c0;
    border-radius: 10px;
    padding: 16px;
    width: 100%;

    border: 2px solid #dcd0c0;
    color: #666360;

    display: flex;
    align-items: center;

    & + input {
      margin-top: 8px;
    }
  }
`;

export const Nav = styled.nav`
  display: flex;
  background: #dcd0c0;
  color: #c0b283;
  height: 36px;
  flex: 1;
  justify-content: space-between;

  ul {
    display: flex;

    /* background: red; */

    align-items: center;
    p {
      display: inline;
      margin-left: 8px;
      color: #373737;
      font-size: 18px;
      font-weight: 500;
    }

    & + ul {
      margin-right: 16px;
    }
  }

  li {
    /* background: blue; */
    display: inline;

    list-style: none;
    margin-left: 16px;

    a {
      font-weight: 300;
      text-decoration: none;
      color: #fff;

      &:hover {
        color: ${shade(0.2, '#373737')};
      }
    }
  }
`;
