import styled from 'styled-components';

export const Container = styled.div`
  height: 100vh;
  display: flex;
`;

export const Content = styled.div``;

export const Nav = styled.nav`
  background: #373737;
  color: #c0b283;
  height: 66px;
  flex: 1;

  ul {
    display: flex;
    margin-top: 8px;
    /* background: red; */
    justify-content: space-between
    align-items: center;
    p {
      margin-left: 8px;
      color: #c0b283;
      font-size: 16px;
    }

    & + ul {
      margin-top: 4px;
    }
  }

  li {
    /* background: blue; */
    display: inline;

    list-style: none;
    margin-left: 16px;

    a {
      font-weight: 300;
      text-decoration: none;
      color: #fff;
    }
  }
`;
